columns.formula:
// USE THE "SPECIAL BANNER" IF IT EXISTS
IfBlank(
  [Special Banner],
  // USE THE BEST EVENT PHOTO, IF IT EXISTS
  ifblank(
    [Event Photos].filter(Event = thisRow)
      .sort(
        false, Favorite
      )
      .SORT(
        FALSE, [Best Ever]
      )
      .first()
      .Photo,
    // USE THE EVENT FLYER IF IT EXISTS
    ifblank(
      [Event Flyer],
      // OTHERWISE, USE THE MOST RECENT BANNER IMAGE FROM EVENT RESOURCES
      [Event Resources]
        .filter(
          [Resource Type].Contains([Banner Image])
        )
        .first()
        .Image
    )
  )
)

// GET BEST 4 PHOTOS FROM EACH PAST EVENT
  Events
    .filter(
      Topic.contains(Topic) and CurrentValue!=thisRow
    )
    .FormulaMap(
      CurrentValue
        .WithName(
          currentEvent,
          [Event Photos DB]
            .filter(
              Event.Contains(currentEvent)
            )
            .sort(
              False, Favorite
            )
            .sort(
              false, [Best Ever]
            )
            .Slice(
              1, 4
            )
        )
    )
    .ListCombine()
